import React from "react";
import Pagination from "@mui/material/Pagination";
import { usePagination } from "./../hooks/pagination";
import { Grid , CardMedia, Card , CardActions , CardContent , Button , Typography } from "@mui/material";

const Pages = ({ data }) => {
  const [
    totalPages,
    startPageIndex,
    endPageIndex,
    currentPageIndex, //eslint-disable-line
    displayPage,
  ] = usePagination(5, data.length);
  return (
    // <Grid item xs={6} key={"publishedAt"}>
    <div style={{ marginLeft: "20px" }}>
    <h1>User Posts</h1>
    {(() => {
      const displayPosts = [];
      for (let i = startPageIndex; i <= endPageIndex; i++) {
        displayPosts.push(
        //   <Grid item xs={3} key={key}>
        <Card sx={{ maxWidth: 345 }} key={data[i].publishedAt}>
        <CardMedia
          component="img"
          height="140"
          image={data[i].urlToImage}
          alt="green iguana"
        />
        <CardContent>
          <Typography gutterBottom variant="h4" component="div">
          {data[i].author}
          </Typography>
          <Typography gutterBottom variant="h7" component="div">
          {data[i].title}
          </Typography>
          
          <Typography variant="body2" color="text.secondary">
          {data[i].description}

          </Typography>
        </CardContent>
        <CardActions>
          {/* <Button size="small">Share</Button> */}
          <Button size="small">Learn More</Button>
        </CardActions>
      </Card>
  
      

          );
        }
        return displayPosts;
      })()}
      <Pagination
        color="primary"
        count={totalPages}
        onChange={(event, value) => displayPage(value)}
      />
    </div>
    // </Grid>

  );
};

export default Pages;
